import logo from './logo.svg';
import './App.css';
import {useState, useRef, useEffect} from "react";
import InputGroup from "./components/InputGroup";
import TodoItem from "./components/TodoItem";
import EventsListing from "./components/EventsListing";
import StarredEvents from "./components/StarredEvents";



function App() {
    let defaultTodoItems = [
        {
            task: 'laundry',
            created: 1647999964830,
        }
    ];
    const [umichEvents, setUmichEvents] = useState([]);

    const [todoItems, setTodoItems] = useState(defaultTodoItems);
    const [starredEvents, setStarredEvents] = useState([])
    const [taskDescription, setTaskDescription] = useState('');
    const inputRef = useRef();



    const removeTodoItem = (created) => {
        setTodoItems((previousTodoItems) => {
            // Keep all items that dont match `timestamp`.
            const withItemRemoved = previousTodoItems.filter((item) => {
                return item.created !== created
            });
            localStorage.setItem('my-todo-items', JSON.stringify(withItemRemoved));

            return withItemRemoved;
        });
    }

    function showRhymingWordsBtn () {
        console.log('input: ', inputRef.current.value)
        setTaskDescription(inputRef.current.value)
        console.log('taskDescription', taskDescription)
        // console.log(`https://api.datamuse.com/words?rel_rhy=${taskDescription}`)
        fetch(`https://api.datamuse.com/words?rel_rhy=${taskDescription}`)
            .then((response) => response.json())
            .then((json) => setUmichEvents(Object.values(json)));
        // setTaskDescription('')
        //
        // inputRef.current.value = '';
        // inputRef.current.focus();
    }

    function datamuseRequest(url, callback) {
        fetch(url)
            .then((response) => response.json())
            .then((data) => {
                // This invokes the callback that updates the page.
                callback(data);
            }, (err) => {
                console.error(err);
            });
    }

    // useEffect(() => {
    //
    // }, []);

    return (
      <main className="container">
        <h1 className="row">React Rhyme Finder (579 Problem Set 6)</h1>

          {/*<div className="row">*/}
          {/*    <div className="col">Saved words: <span></span></div>*/}
          {/*</div>*/}

          <StarredEvents starredList={starredEvents} />

          {/*<section>*/}
          {/*  <InputGroup setTodoItems = {setTodoItems} />*/}
          {/*</section>*/}

          <div className="input-group">
              <input
                  ref = {inputRef}
                  // value={taskDescription}
                  // onChange={(e) => setTaskDescription(e.target.value)}
                  className="form-control w-25"
                  type="text"
                  placeholder="Enter a word"
                  aria-label="Enter a word"
                  // onKeyDown={keyDownHandler}
                  />
              <button
                  onClick={() => showRhymingWordsBtn}
                  className="btn btn-primary"
                  aria-label="Show rhyming words">
                  Show rhyming words
              </button>
              <button
                  // onClick={showSynonymsBtn}
                  className="btn btn-secondary"
                  aria-label="Show synonyms">
                  Show synonyms
              </button>
          </div>

          {/*<div className="row">*/}
          {/*    <div className="input-group col">*/}
          {/*        <input className="form-control" type="text" placeholder="Enter a word" id="word_input"/>*/}
          {/*        <button id="show_rhymes" type="button" className="btn btn-primary">Show rhyming words</button>*/}
          {/*        <button id="show_synonyms" type="button" className="btn btn-secondary">Show synonyms</button>*/}
          {/*    </div>*/}
          {/*</div>*/}

          {/*<div className="row">*/}
          {/*    <h2 className="col"></h2>*/}
          {/*</div>*/}
          {/*<div className="output row">*/}
          {/*    <output className="col"></output>*/}
          {/*</div>*/}

        <ul className="row">
          {umichEvents.map((item) =>
              <EventsListing events={umichEvents}
                  // task={item.task}
                  // timestamp={item.timestamp}
                  // created={item.created}
                  // key={item.created}
                  // remove={() => removeTodoItem(item.created)}
              />
          )}
        </ul>
      </main>
  );
}

export default App;
