// import './EventsListing.css';
// import EventDateTime from "./EventDateTime";
import EventInfo from "./EventInfo";
import EventInstance from "./EventInstance";
import StarredEvents from "./StarredEvents";
import {useState} from "react";

const NumSyllableGroup = (props) => {
    const [starredEvents, setStarredEvents] = useState([])

    /**
     * Creates an array where each item is the JSX "markup" for an event.
     *
     * @returns {*[]}
     */

    const generateEvents = () => {
        // Initialize an empty array that will get each event
        const eventsToShow = [];


        function groupBy(objects, property) {
            // If property is not a function, convert it to a function that accepts one argument (an object) and returns that object's
            // value for property (obj[property])
            if(typeof property !== 'function') {
                const propName = property;
                property = (obj) => obj[propName];
            }

            const groupedObjects = new Map(); // Keys: group names, value: list of items in that group
            for(const object of objects) {
                const groupName = property(object);
                //Make sure that the group exists
                if(!groupedObjects.has(groupName)) {
                    groupedObjects.set(groupName, []);
                }
                groupedObjects.get(groupName).push(object);
            }

            // Create an object with the results. Sort the keys so that they are in a sensible "order"
            const result = {};
            for(const key of Array.from(groupedObjects.keys()).sort()) {
                result[key] = groupedObjects.get(key);
            }
            return result;
        }

        // Loop through the event list. Add each event as new array item.
        // If React sees an array of JSX "markup", it will render each one.
        props.events.forEach((eventInstance, index) =>
            eventsToShow.push(
                <EventInstance
                    setStarredEvents = {setStarredEvents} //will be passing down to EventInstance
                    key={index}
                    title={eventInstance.word}
                >
                    <EventInfo
                        title={eventInstance.word}
                        // description={eventInstance.description}
                    />
                </EventInstance>
            )
        );
        return eventsToShow;
    }



    const length = Object.keys(groupBy(props.events, 'numSyllables')).length;

    for (let m = 0; m<length; m++) {
        console.log(Object.keys(groupBy(props.events, 'numSyllables'))[m])
        for (let item in groupBy(props.events, 'numSyllables')[Object.keys(groupBy(props.events, 'numSyllables'))[m]]) {
            const wordTake = groupBy(props.events, 'numSyllables')[Object.keys(groupBy(props.events, 'numSyllables'))[m]][item].title;
            console.log(wordTake)
        }
    }

    //API 다운로딩할때 로딩화면
    if (props.events.length > 0) {
        return (
            <>
                {/*<StarredEvents starredList={starredEvents} />*/}
                <div className='events'>
                    {generateEvents()}
                    {/*    {props.events.map((eventInstance, index) => (*/}
                    {/*        <li>*/}
                    {/*            {eventInstance.word}*/}
                    {/*            <button*/}
                    {/*                className="btn btn-sm btn-outline-success done"*/}
                    {/*                type="button"*/}
                    {/*                // onClick={remove}*/}
                    {/*            >*/}
                    {/*                (save)*/}
                    {/*            </button>*/}
                    {/*        </li>*/}
                    {/*        )*/}
                    {/*    )}*/}
                </div>
            </>
        );
    } else {
        return <h2 className='events--loading'>LOADING!!</h2>
    }

}

export default NumSyllableGroup;
